	public static function get_details($report_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="SELECT c.comp_def_name,s.scale_name,s.scale_number,a.* FROM uls_assessment_employee_dev_report a
		left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) c on c.comp_def_id=a.comp_def_id
		left join(SELECT `scale_id`,`scale_name`,scale_number FROM `uls_level_master_scale`) s on s.scale_id=a.scale_id
		WHERE a.dev_report_id=".$report_id;
		$testdetails=UlsMenu::callpdorow($query);
        return $testdetails;
    }
	
