	static function report_bytype_competencies($ass_id,$pos_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		$query="SELECT c.comp_def_id,c.comp_def_name,ass.scale_number,c.comp_def_category, f.comp_cri_name,a.* FROM `uls_assessment_competencies` a 
		left join (select comp_def_id,comp_def_name,comp_def_short_desc,`comp_def_category` from uls_competency_definition group by comp_def_id)c on c.comp_def_id=a.assessment_pos_com_id
		left join(SELECT `comp_position_competency_id`,`comp_position_criticality_id`,`comp_position_id` FROM `uls_competency_position_requirements`) p on p.comp_position_competency_id=a.assessment_pos_com_id
		left join (select comp_cri_id,comp_cri_name from uls_competency_criticality group by comp_cri_id)f on f.comp_cri_id=p.comp_position_criticality_id
		left join (select scale_id,scale_name,scale_number from uls_level_master_scale group by scale_id)ass on ass.scale_id=a.assessment_pos_level_scale_id
		where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.assessment_id=".$ass_id." and a.position_id=".$pos_id." and a.pos_com_weightage!=0 order by c.comp_def_name ASC";
		$compdef=UlsMenu::callpdo($query);
		return $compdef;
	}
	
