	static function assessment_employee_assessor_report($ass_id){
		$query="select b.`employee_id`,b.`employee_number`,b.`full_name`,b.`org_name`,b.`position_name`,b.`location_name` ,m.assessor_name,a.* from uls_assessment_report_final a 
		left join(SELECT `employee_id`,`employee_number`,`full_name`,`org_name`,`position_name`,`location_name` FROM `employee_data`) b on b.employee_id=a.employee_id
		left join(SELECT `assessor_id`,`assessor_name` FROM `uls_assessor_master`) m on m.assessor_id=a.assessor_id
		where a.assessment_id=".$ass_id." and a.status='A' ";
