	static function get_assessment_assessor_final($id,$pos_id,$emp_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			echo $query="select a.*,c.rating_number from uls_assessment_report_final a
			inner join(SELECT `assessor_id` FROM `uls_assessment_position_assessor`) b on a.assessor_id=b.assessor_id
			left join(SELECT `scale_id`,`rating_id`,`rating_number`,`rating_name_scale` FROM `uls_rating_master_scale`) c on c.scale_id=a.scale_id
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.assessment_id=$id and a.position_id=$pos_id and a.employee_id=$emp_id ";
			$compdef=UlsMenu::callpdorow($query);
		}
