	public static function getbeiassessment($test){
		$query="SELECT a.*,c.instrument_name FROM uls_assessment_test_behavorial_inst a
		inner join (select instrument_id,instrument_name from uls_be_instruments) c on a.instrument_id=c.instrument_id
		WHERE a.assess_test_id=".$test;
		$testdetails=UlsMenu::callpdo($query);
        return $testdetails;
    }	
	
	static function getbehavorialassessment($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select c.value_name,a.*,i.instrument_name from uls_assessment_test_behavorial_inst a
			left join (SELECT `instrument_id`,instrument_name FROM `uls_be_instruments`) i on i.instrument_id=a.instrument_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND c.value_code=a.status
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.assess_test_id=$id order by a.	beh_inst_id asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
