			left join (SELECT `instrument_id`,instrument_name,instrument_type FROM `uls_be_instruments`) i on i.instrument_id=a.instrument_id
			left join(SELECT `ins_subpara_id`,`ins_para_id`,`instrument_id`,`ins_subpara_code`,`ins_subpara_text`,`subpara_left`,`ins_subpara_text_ext`,`subpara_right` FROM `uls_be_ins_subparameters`) s on s.instrument_id=i.instrument_id
