	static function getinbasketassessment($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select c.value_name,t.test_name,a.*,t.inbasket_id,i.inbasket_name,ic.comp_id,dc.comp_def_name from uls_assessment_test_inbasket a 
			left join (SELECT `test_id`,`test_name`,inbasket_id FROM `uls_competency_test`) t on t.test_id=a.test_id
			left join (SELECT `inbasket_name`,inbasket_id FROM `uls_inbasket_master`) i on i.inbasket_id=t.inbasket_id
			left join (SELECT distinct(`inbasket_competencies`) as comp_id,inbasket_id FROM `uls_inbasket_competencies`) ic on i.inbasket_id=ic.inbasket_id
			left join (SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) dc on ic.comp_id=dc.comp_def_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND c.value_code=a.status
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.assess_test_id=$id order by a.inb_assess_test_id asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
