	static function gettestquestions($assess_test_id,$test_id,$test_type,$emp_id,$ass_id){
		$sql="SELECT group_concat(distinct(a.`question_id`)) as q_id,a.`competency_id`,count(*)  as q_count,d.comp_def_name,s.scale_name,a.position_id,d.comp_def_id,s.scale_id,ac.assessment_pos_level_id,r.report_id,rs.assessed_scale_id,s.scale_number FROM `uls_assessment_test_questions` a
		left join(SELECT `assessment_id`,`assessment_pos_com_id`,`assessment_pos_level_scale_id`,`assessment_type`,assessment_pos_level_id,position_id FROM `uls_assessment_competencies` where assessment_id=".$ass_id.") ac on ac.assessment_pos_com_id=a.competency_id and ac.position_id=a.position_id
		left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) d on d.comp_def_id=ac.assessment_pos_com_id
		left join (SELECT `scale_id`,`scale_name`,scale_number FROM `uls_level_master_scale`) s on s.scale_id=ac.assessment_pos_level_scale_id
		left join (select report_id,assessment_id,position_id,competency_id,assessed_scale_id,development_area,employee_id,assessor_id from uls_assessment_report where employee_id=$emp_id and assessment_id=".$ass_id." and assessor_id=".$_SESSION['asr_id'].")r on r.assessment_id=ac.assessment_id and r.position_id=ac.position_id and r.competency_id=ac.assessment_pos_com_id 
		left join (select report_id,assessed_scale_id,assessment_type from uls_assessment_report_bytype where assessment_type='$test_type' and assessment_id=".$ass_id.")rs on rs.report_id=r.report_id
		WHERE `assess_test_id`=".$assess_test_id." and `test_id`=".$test_id." and a.assessment_id=".$ass_id." group by `competency_id` ";
		return UlsMenu::callpdo($sql);
	}
	
