	public static function get_question_count_ass($id){
		$testdetails=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT count(assessment_test_id) as te_count FROM uls_assessment_test_questions a
			WHERE a.assess_test_id=".$id;
			$testdetails=UlsMenu::callpdorow($query);
		}
		return $testdetails;
    } 
	
	public static function get_question_val($id,$assess_test_id){
		$testdetails=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT *,e.comp_def_id,e.comp_def_name,f.scale_name,f.scale_id FROM uls_assessment_test_questions a
			left join (select question_id,question_name,level_id,question_type,scale_id from uls_questions) c on a.question_id=c.question_id
			left join (select comp_def_id,comp_def_name,comp_def_level from uls_competency_definition) e on a.competency_id=e.comp_def_id
			left join (select scale_id,scale_name,level_id from uls_level_master_scale) f on c.level_id=f.level_id and c.scale_id=f.scale_id
			WHERE a.assessment_test_id=".$id." and a.assess_test_id=".$assess_test_id;
			$testdetails=UlsMenu::callpdorow($query);
		}
		return $testdetails;
    }
	
	public static function get_question_ids($assess_test_id,$comp_def_id){
		$testdetails=array();
		if(!empty($assess_test_id) && is_numeric($assess_test_id)){
			$query="SELECT count(assessment_test_id) as te_count,group_concat(a.question_id) as question_ids FROM uls_assessment_test_questions a
			WHERE a.assess_test_id=".$assess_test_id." and a.competency_id=".$comp_def_id;
			$testdetails=UlsMenu::callpdorow($query);
		}
		return $testdetails;
    }
	
