	static function get_interview_questions($pos_id,$test_type,$emp_id,$ass_id){
		$sql="SELECT d.comp_def_name,s.scale_name,ac.position_id,d.comp_def_id,s.scale_id,ac.assessment_pos_level_id,r.report_id,rs.assessed_scale_id,s.scale_number,r.development_area,rs.interview_ass_value FROM `uls_assessment_competencies` ac 
		left join(SELECT `comp_def_id`,`comp_def_name`,comp_def_category FROM `uls_competency_definition`) d on d.comp_def_id=ac.assessment_pos_com_id
		left join (SELECT `scale_id`,`scale_name`,scale_number FROM `uls_level_master_scale`) s on s.scale_id=ac.assessment_pos_level_scale_id
		left join (select report_id,assessment_id,position_id,competency_id,assessed_scale_id,development_area,employee_id,assessor_id from uls_assessment_report where employee_id=$emp_id and assessment_id=".$ass_id." and assessor_id=".$_SESSION['asr_id'].")r on r.assessment_id=ac.assessment_id and r.position_id=ac.position_id and r.competency_id=ac.assessment_pos_com_id 
		left join (select report_id,assessed_scale_id,assessment_type,interview_ass_value from uls_assessment_report_bytype where assessment_type='$test_type' and assessment_id=".$ass_id.")rs on rs.report_id=r.report_id
		WHERE ac.position_id=".$pos_id." and ac.assessment_id=".$ass_id."";
		//and d.comp_def_category!=10
		return UlsMenu::callpdo($sql);
	}
	
	
	
