	static function get_count_test($comp_id,$scale_id,$id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		$query="select count(a.assessment_test_id) as test_count from uls_assessment_test_questions a
		left join(SELECT `question_id`,`question_bank_id`,`scale_id` FROM `uls_questions` ) b on a.question_id=b.question_id
		left join(SELECT `question_bank_id`,`comp_def_id` FROM `uls_questionbank`) c on c.question_bank_id=b.question_bank_id
		where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.competency_id=".$comp_id." and b.scale_id=".$scale_id." and a.position_id in (".$id.") group by a.question_id ";
		$compdef=UlsMenu::callpdo($query);
		return $compdef;
	}
	
