class UlsBeInstruments extends BaseUlsBeInstruments
{
	static function viewinstruments(){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="select a.* from uls_be_instruments a
		where 1 ";
		$compdef=UlsMenu::callpdo($query);
		return $compdef;
	}
	
	static function view_instruments(){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="select a.* from uls_be_instruments a
		where a.instrument_status='A' ";
		$compdef=UlsMenu::callpdo($query);
		return $compdef;
	}
	
	static function searchcount(){
		$CI =& get_instance();
		$CI->load->library('session');
		$learning="select count(*) as num_count from uls_be_instruments where 1 ";
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
	
	static function search($start,$limit){
		$CI =& get_instance();
		$CI->load->library('session');
		$learnings="select b.value_name as int_status, a.* from uls_be_instruments a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.instrument_status = b.value_code 
		where 1 limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
	static function view_instrument_details($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select a.* from uls_be_instruments a
			where a.instrument_status='A' and a.instrument_id=".$id;
			$compdef=UlsMenu::callpdorow($query);
		}
		return $compdef;
	}
	
}