	static function getcasestudycompetencies($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select b.comp_def_name as competencies_name, c.scale_name as level_name, a.* from uls_case_study_competencies a
			left join (select comp_def_id,comp_def_name from uls_competency_definition group by comp_def_id)b on b.comp_def_id=a.casestudy_competencies
			left join (select scale_id,scale_name from uls_level_master_scale group by scale_id)c on c.scale_id=a.casestudy_scale
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.casestudy_id=$id order by casestudy_competencies asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	static function getcasestudycompetencies_ass($id,$scale_id){
