			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.casestudy_scale=".$scale_id." and a.casestudy_competencies=$id order by casestudy_competencies asc";
			$compdef=UlsMenu::callpdo($query);
		}
