	static function searchcount($name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and casestudy_name like '%".$name."%'":" and casestudy_name like '%".$name."%'":"";
		$learning="select count(*) as num_count from uls_case_study_master where 1 and  parent_org_id=".$CI->session->userdata('parent_org_id').$sq;
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
    
    static function search($start,$limit,$name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and a.casestudy_name like '%".$name."%'":" and a.casestudy_name like '%".$name."%'":"";
		$learnings="select b.value_name as casestudy,c.value_name as casestatus,a.* from uls_case_study_master a 
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'CASESTUDY_TYPE' AND a.casestudy_type = b.value_code
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND a.casestudy_status = c.value_code
		where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." $sq limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
