	static function get_casestudy_competency_unique_assessor($id,$test_type,$emp_id,$ass_id,$pos_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="SELECT b.`casestudy_quest_id`,b.`casestudy_id`,c.casestudy_name,b.level_id,qi.position_id,d.comp_def_name,l.scale_name,l.scale_id,d.comp_def_id,r.report_id,rs.assessed_scale_id,qi.case_assess_test_id FROM `uls_assessment_competencies` a 
		left join(SELECT `casestudy_quest_id`,`casestudy_id`,`comp_def_id`,`scale_id`,level_id FROM `uls_case_study_comp_map`) b on a.assessment_pos_com_id=b.comp_def_id and a.assessment_pos_level_scale_id=b.scale_id
		left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) d on d.comp_def_id=b.comp_def_id
		LEFT JOIN(SELECT `scale_id`,`scale_number`,`level_id`,`scale_name`, `description` as scale_desc FROM `uls_level_master_scale`)l on l.scale_id=a.assessment_pos_level_scale_id
		left join(SELECT `casestudy_id`,`casestudy_name` FROM `uls_case_study_master`) c on c.casestudy_id=b.casestudy_id
		LEFT join(SELECT case_assess_test_id,`assess_test_id`,`assessment_pos_id`,`position_id`,`assessment_id`,`test_id`,`casestudy_id`,`assessment_type` FROM `uls_assessment_test_casestudy` where case_assess_test_id=$id) qi on qi.casestudy_id=b.casestudy_id
		left join (select report_id,assessment_id,position_id,competency_id,assessed_scale_id,development_area,employee_id,assessor_id from uls_assessment_report where employee_id=".$emp_id." and assessment_id=".$ass_id." and assessor_id=".$_SESSION['asr_id'].")r on r.assessment_id=qi.assessment_id and r.position_id=qi.position_id and r.competency_id=b.comp_def_id
		left join (select report_id,assessed_scale_id,assessment_type,case_assess_test_id from uls_assessment_report_bytype where assessment_type=$test_type and assessment_id=".$ass_id." and case_assess_test_id=".$id.")rs on rs.report_id=r.report_id and qi.case_assess_test_id=rs.case_assess_test_id
		where(a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.assessment_id=".$ass_id." and a.position_id=".$pos_id." and qi.case_assess_test_id=$id) group by b.comp_def_id";
        return UlsMenu::callpdo($query);
	}
	
