class UlsCompetencyMigrationContentProgram extends BaseUlsCompetencyMigrationContentProgram
{
	function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');
		$this->created_by=$CI->session->userdata('username');
		$this->created_date=date('Y-m-d');
		$this->modified_by=$CI->session->userdata('username');        
		$this->modified_date=date('Y-m-d');
		$this->last_modified_by=$CI->session->userdata('user_id');
	}

	function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
		$this->modified_date=date('Y-m-d');
		$this->modified_by=$CI->session->userdata('username');
		$this->last_modified_by=$CI->session->userdata('user_id');
	}
	
	static function migration_competencies($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$level=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT a.* FROM uls_competency_migration_content_program a WHERE 1 and a.course_id=".$id;
			$level=UlsMenu::callpdo($query);
		}
		return $level;
	}
	
	static function migration_comp($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$level=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT c.comp_def_name,s.scale_name,b.value_name as status_val FROM uls_competency_migration_content_program a
			left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) c on c.comp_def_id=a.comp_def_id
			left join(SELECT `scale_id`,`scale_name` FROM `uls_level_master_scale`) s on s.scale_id=a.scale_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code	
			WHERE 1 and a.course_id=".$id;
			$level=UlsMenu::callpdo($query);
		}
		return $level;
	}
}