class UlsFishboneCompMap extends BaseUlsFishboneCompMap
{
	function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_by=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function getfishbonecompetencies($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select b.comp_def_name as competencies_name, c.scale_name as level_name, a.* from uls_fishbone_comp_map a
			left join (select comp_def_id,comp_def_name from uls_competency_definition group by comp_def_id)b on b.comp_def_id=a.comp_def_id
			left join (select scale_id,scale_name from uls_level_master_scale group by scale_id)c on c.scale_id=a.scale_id
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.fishbone_quest_id=$id order by a.comp_def_id asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	static function getfishbonecompetencies_count($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id)){
			$query="select a.* from uls_fishbone_comp_map a where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.comp_def_id in ($id) order by a.comp_def_id asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
}