class UlsInbasketCompetencies extends BaseUlsInbasketCompetencies
{
	 function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_by=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function getinbasketcompetencies($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select b.comp_def_name as competencies_name, c.scale_name as level_name, a.* from uls_inbasket_competencies a
			left join (select comp_def_id,comp_def_name from uls_competency_definition group by comp_def_id)b on b.comp_def_id=a.inbasket_competencies
			left join (select scale_id,scale_name from uls_level_master_scale group by scale_id)c on c.scale_id=a.inbasket_scale
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.inbasket_id=$id order by a.inbasket_competencies asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	static function getinbusketcompetencies_ass($id,$scale_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select a.*,b.inbasket_element_name from uls_inbasket_competencies a
			left join (select inbasket_id,inbasket_element_name,inbasket_element_id from uls_inbasket_elements group by inbasket_element_id)b on b.inbasket_id=a.inbasket_id
			where 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.inbasket_scale=".$scale_id." and a.inbasket_competencies=$id order by inbasket_competencies asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	
}