        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function searchcount($name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and inbasket_name like '%".$name."%'":" and inbasket_name like '%".$name."%'":"";
		$learning="select count(*) as num_count from uls_inbasket_master where 1 and  parent_org_id=".$CI->session->userdata('parent_org_id').$sq;
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
    
    static function search($start,$limit,$name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and a.inbasket_name like '%".$name."%'":" and a.inbasket_name like '%".$name."%'":"";
		$learnings="select c.value_name as inbasketstatus,a.* from uls_inbasket_master a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND a.inbasket_status = c.value_code
		where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." $sq limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
	static function viewinbasket($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select c.value_name as inbasketstatus,d.value_name as inbasketaction,f.value_name as inbasketscortingorder,a.*,q.question_bank_id,qs.question_id,qs.question_name,p.position_name from uls_inbasket_master a
			LEFT JOIN (SELECT `question_bank_id`,`inbasket_id` FROM `uls_questionbank`)q ON a.inbasket_id = q.inbasket_id
			LEFT JOIN (SELECT `position_id`,`position_name` FROM `uls_position`)p ON a.position_id = p.position_id
			LEFT JOIN (SELECT `question_id`,`question_bank_id`,question_name FROM `uls_questions`)qs ON q.question_bank_id = qs.question_bank_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)d ON d.master_code = 'YES_NO' AND a.inbasket_action = d.value_code
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)f ON f.master_code = 'YES_NO' AND a.inbasket_scorting_order = f.value_code
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND a.inbasket_status = c.value_code
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.inbasket_id=$id";
			$compdef=UlsMenu::callpdorow($query);
		}
		return $compdef;
	}
	
