class UlsKraMasterCompetency extends BaseUlsKraMasterCompetency
{
	function preInsert($event){
		 $CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_by=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function kra_competencies($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$level=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT a.* FROM uls_kra_master_competency a WHERE 1 and a.kra_master_id=".$id." and a.parent_org_id=".$CI->session->userdata('parent_org_id');
			$level=UlsMenu::callpdo($query);
		}
		return $level;
	}
	
	static function viewkracomp($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$level=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT b.value_name as comp_status, c.comp_def_name FROM uls_kra_master_competency a 
			LEFT JOIN (SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) c on c.comp_def_id=a.comp_def_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code 
			WHERE 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.kra_master_id=$id";
			$level=UlsMenu::callpdo($query);
		}
		return $level;
	}
}