class UlsLevelMaster extends BaseUlsLevelMaster
{
	 function preInsert($event){
		 $CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_by=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function searchcount($level=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($level)? !empty($sq)? " and level_name like '%".$level."%'":" and level_name like '%".$level."%'":"";
		$learning="select count(*) as num_count from uls_level_master where 1 and  parent_org_id=".$CI->session->userdata('parent_org_id').$sq;
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
    
    static function search($start,$limit,$level=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($level)? !empty($sq)? " and a.level_name like '%".$level."%'":" and a.level_name like '%".$level."%'":"";
		$learnings="select b.value_name as level_status, a.* from uls_level_master a 
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code 
		where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." $sq limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
	static function viewlevel($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$level=array();
		if(!empty($id) && is_numeric($id)){
			$query="SELECT b.value_name as level_status, a. * FROM uls_level_master a 
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code 
			WHERE 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.level_id=$id";
			$level=UlsMenu::callpdorow($query);
		}
		return $level;
	}
	
	static function levels(){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="SELECT a. * FROM uls_level_master a WHERE 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id');
		$level=UlsMenu::callpdo($query);
		return $level;
	}
}