			$query="SELECT s.scale_name,s.scale_id,ac.`pos_comp_tray_id`,ac.`val_id`,ac.`position_id`,ac.`employee_id`,ac.`category_id`,ac.`competency_name`,ac.`require_scale_id`,ac.`reason` FROM `uls_position_competency_temp_tray` ac 
