	static function get_position_validation_competencies($val_id,$pos_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($val_id) && is_numeric($val_id)){
			$query="SELECT d.full_name, c.name,  s.scale_name, s.scale_id, ac.`pos_comp_tray_id`, ac.`val_id`, ac.`position_id`, ac.`employee_id`, ac.`category_id`,ac.`competency_name`,ac.`require_scale_id`,ac.`reason` FROM `uls_position_competency_temp_tray` ac 
			inner join (select scale_id,scale_name from uls_level_master_scale group by scale_id)s on s.scale_id=ac.require_scale_id
			inner join(SELECT `category_id`,`name` FROM `uls_category`) c on ac.category_id=c.category_id
			inner join employee_data d on ac.employee_id=d.employee_id  
			where 1 and ac.parent_org_id=".$CI->session->userdata('parent_org_id')." and ac.val_id=".$val_id." and ac.position_id=".$pos_id." order by ac.category_id asc";
			$compdef=UlsMenu::callpdo($query);
		}
