	function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');  
	    if($this->active_flag=='P'){
			$this->start_date_active=date('Y-m-d');
			$this->end_date_active=NULL;
		}
		if($this->active_flag=='N'){
