	public static function searchcount($question_bank,$qtype){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq="";
        $sq=!empty($question_bank)? " and a.name like '%$question_bank%'":"";
		$sq.=!empty($qtype)? " and a.type='$qtype'":" and (a.type='COMP_TEST' || a.type='COMP_INTERVIEW')";
		$query="SELECT count(*) as numcount FROM uls_questionbank a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON master_code = 'PUBLISH_STATUS' AND a.active_flag = b.value_code 
		LEFT JOIN (SELECT count(*) as questioncount, question_bank_id FROM uls_questions GROUP BY question_bank_id)c ON c.question_bank_id = a.question_bank_id 
		WHERE 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.active_flag='P' $sq ";
		$masterlist=UlsMenu::callpdorow($query);		
		return $masterlist['numcount'];
    }
    
    public static function search($start, $limit,$question_bank,$qtype){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq="";
        $sq.=!empty($question_bank)? " and a.name like '%$question_bank%'":"";
		$sq.=!empty($qtype)? " and a.type='$qtype'":" and (a.type='COMP_TEST' || a.type='COMP_INTERVIEW')";
		$query="SELECT c.questioncount,b.value_name as publish_status,d.value_name as ass_type, a. * FROM uls_questionbank a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'PUBLISH_STATUS' AND a.active_flag = b.value_code 
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)d ON d.master_code = 'ASSESSMENT_TYPE' AND a.type = d.value_code 
		LEFT JOIN (SELECT count(*) as questioncount, question_bank_id FROM uls_questions GROUP BY question_bank_id)c ON c.question_bank_id = a.question_bank_id 
		WHERE 1 and a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.active_flag='P' $sq  order by a.name ASC  limit $limit offset $start";
		$masterlist=UlsMenu::callpdo($query);
        return $masterlist;
    }
	
