	static function view_questionbank($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select b.value_name as comp_def_status,d.value_name as types,a.*,c.comp_def_name from uls_questionbank a
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'PUBLISH_STATUS' AND a.active_flag = b.value_code
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)d ON d.master_code = 'ASSESSMENT_TYPE' AND a.type = d.value_code
			LEFT JOIN (SELECT comp_def_id, comp_def_name FROM uls_competency_definition GROUP BY comp_def_id)c ON a.comp_def_id = c.comp_def_id
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.question_bank_id=$id";
			$compdef=UlsMenu::callpdorow($query);
		}
		return $compdef;
	}
	
