	static function get_question_csv($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$query="select d.type_name,s.scale_name,a.question_bank_id, a.question_id as id,a.question_name as question_name,a.question_type as question_type,a.points,a.sequence,a.active_flag from uls_questions a
		left join(SELECT `scale_id`,`scale_name`,scale_number FROM `uls_level_master_scale` group by scale_id)s on s.scale_id=a.scale_id
		left join(SELECT type_flag,type_name FROM `uls_questions_type` group by type_id)d on a.question_type=d.type_flag
		where a.question_bank_id=".$id." and a.parent_org_id=".$CI->session->userdata('parent_org_id');
		$group=UlsMenu::callpdo($query);
		return $group;
	}
	
