			left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) cd on cd.comp_def_id=a.ques_competency_id
			left join(SELECT `scale_id`,`scale_number`,`scale_name` FROM `uls_level_master_scale`) s on s.scale_id=a.ques_level_scale_id
			where 1 and a.ques_id=$id";
