	static function view_comptency_element($ques_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($ques_id) && is_numeric($ques_id)){
			//group by e.element_id
			$query="select cd.`comp_def_id`,cd.`comp_def_name`,s.`scale_id`,s.`scale_name`,c.`ques_element_id`,c.`element_id` as element_ids ,a.ques_competency_id,a.ques_level_scale_id,e.element_id,e.element_name from uls_questionnaire_competency a
			left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) cd on cd.comp_def_id=a.ques_competency_id
			left join(SELECT `scale_id`,`scale_number`,`scale_name` FROM `uls_level_master_scale`) s on s.scale_id=a.ques_level_scale_id
			left join(SELECT `element_id`,`comp_def_id`,`scale_id`,`element_name`,`status` FROM `uls_competency_def_level_elements` where status='A') e on e.comp_def_id=a.ques_competency_id and e.scale_id=a.ques_level_scale_id
			LEFT JOIN (SELECT `ques_element_id`,`ques_id`,`element_id`,`element_competency_id`,`element_level_scale_id` FROM `uls_questionnaire_competency_element`)c ON c.ques_id = a.ques_id and c.element_competency_id=a.ques_competency_id and c.element_level_scale_id=a.	ques_level_scale_id	and c.element_id=e.element_id
			where 1 and a.ques_id=$ques_id order by e.element_name asc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	static function edit_comptency_element($ques_id){
