			$query="select a.*,e.element_name,cd.`comp_def_name`,s.`scale_name` from uls_questionnaire_competency_element a
			left join(SELECT `comp_def_id`,`comp_def_name` FROM `uls_competency_definition`) cd on cd.comp_def_id=a.element_competency_id
			left join(SELECT `scale_id`,`scale_number`,`scale_name` FROM `uls_level_master_scale`) s on s.scale_id=a.element_level_scale_id
			left join(SELECT `element_id`,`comp_def_id`,`scale_id`,`element_name`,`status` FROM `uls_competency_def_level_elements` where status='A') e on e.comp_def_id=a.element_competency_id and e.scale_id=a.element_level_scale_id and e.element_id=a.element_id
			where 1 and a.ques_id=$ques_id";
