class UlsQuestionnaireMaster extends BaseUlsQuestionnaireMaster
{	
	function preInsert($event){
		 $CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_by=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_by=$CI->session->userdata('user_id');
    }
	
	static function searchcount($name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and ques_name like '%".$name."%'":" and ques_name like '%".$name."%'":"";
		$learning="select count(*) as num_count from uls_questionnaire_master where 1 and  parent_org_id=".$CI->session->userdata('parent_org_id').$sq;
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
    
    static function search($start,$limit,$name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and a.ques_name like '%".$name."%'":" and a.ques_name like '%".$name."%'":"";
		$learnings="select c.value_name as qusstatus,a.* from uls_questionnaire_master a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND a.status = c.value_code
		where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." $sq limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
	static function viewquestionnaire($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select c.value_name as questatus,a.*,p.position_name,r.rating_name from uls_questionnaire_master a
			left join(SELECT `position_id`,`position_name` FROM `uls_position`) p on p.position_id=a.position_id
			left join(SELECT `rating_id`,`rating_name` FROM `uls_rating_master`) r on r.rating_id=a.rating_id
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)c ON c.master_code = 'STATUS' AND a.status = c.value_code
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.ques_id=$id";
			$compdef=UlsMenu::callpdorow($query);
		}
		return $compdef;
	}
	
	static function viewfeedback_position($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select a.* from uls_questionnaire_master a
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.position_id=$id";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
	
	static function viewfeedback_rating($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select r.`scale_id`,r.`rating_id`,r.`rating_number`,r.`rating_name_scale`,a.* from uls_questionnaire_master a
			left join(SELECT `scale_id`,`rating_id`,`rating_number`,`rating_name_scale` FROM `uls_rating_master_scale`) r on r.rating_id=a.rating_id
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.ques_id=$id";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
}