class UlsQuestionsLanguage extends BaseUlsQuestionsLanguage
{
	function preInsert($event){
	    $CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }
	
    function preUpdate($event){
	    $CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }
	
	static function get_questionlan_id($id){
		$group=Doctrine_Query::Create()->from('UlsQuestionsLanguage')->where('question_id='.$id)->execute();
		return $group;   
    }
	
	static function get_question_language($id){
		$query="select a.comp_lang_id,a.lang_id,a.lang_question_name as question_name,b.lang_name as language_name  FROM uls_questions_language a, uls_language_master b where( a.lang_id=b.lang_id and  a.question_id=".$id.")";
        return UlsMenu::callpdo($query);
        
    }
	
	static function get_questionlan_name($id){
		$group=Doctrine_Query::Create()->from('UlsQuestionsLanguage')->where('question_id='.$id)->fetchOne();
		return $group;   
    }
	
	static function questionsformigration($ques_id,$lang_id,$questionbank){
		$quest_query="select q.comp_lang_id from uls_questions_language q where q.question_id=".$ques_id." and q.question_bank_id=".$questionbank." and q.lang_id=".$lang_id;
		return UlsMenu::callpdo($quest_query);
	}
}