class UlsSubGrades extends BaseUlsSubGrades
{
	function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');
		$this->parent_org_id=$CI->session->userdata('parent_org_id');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }

    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }
	
	//function to get all the sub grades based on grade id
	public static function subGrades($gradeid){
		if(!empty($gradeid)){
			$CI =& get_instance();
			$CI->load->library('session');
			$data="select a.sub_grade_id, a.grade_id, a.sub_grade_name, a.grade_status from uls_sub_grades a 
			INNER JOIN uls_admin_values v on v.value_code=a.grade_status and v.master_code='STATUS' 
			where a.grade_id=".$gradeid." and a.parent_org_id=".$CI->session->userdata('parent_org_id')."";
			return UlsMenu::callpdo($data);
		}
	}
	//Function to get all the sub grades based on parent organization id
	public static function sug_grades_all(){
		$CI =& get_instance();
		$CI->load->library('session');
		$parent_org_id=$CI->session->userdata('parent_org_id');
		if(!empty($parent_org_id)){
			$sub="select a.sub_grade_id, v.subgrade_name as sub_name from uls_sub_grades a 
			INNER JOIN uls_subgrade v on v.subgrade_id=a.sub_grade_name and v.parent_org_id=a.parent_org_id 
			where a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.grade_status='A'";
			return UlsMenu::callpdo($sub);
		}
	}
	
	public static function get_grade_sub($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$parent_org_id=$CI->session->userdata('parent_org_id');
		if(!empty($parent_org_id)){
			if(!empty($id))
			{
				$sub="select a.sub_grade_id, v.subgrade_name as sub_name from uls_sub_grades a 
				INNER JOIN uls_subgrade v on v.subgrade_id=a.sub_grade_name and v.parent_org_id=a.parent_org_id 
				where a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.grade_status='A' and a.grade_id=$id";
				return UlsMenu::callpdo($sub);
			}
			else{
				return array();
			}
		}
	}
	
}