class UlsTestQuestions extends BaseUlsTestQuestions
{
	function preInsert($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->created_by=$CI->session->userdata('username');
        $this->created_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');        
        $this->modified_date=date('Y-m-d');
        $this->last_modified_id=$CI->session->userdata('user_id');
        $this->parent_org_id=$CI->session->userdata('parent_org_id');
    }
	
    function preUpdate($event){
		$CI =& get_instance();
		$CI->load->library('session');
        $this->modified_date=date('Y-m-d');
        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }
    
	static function get_testquestions($id){ 
	    if(isset($id)){
		$pdo = Doctrine_Manager::getInstance()->getCurrentConnection()->getDbh();
		$emp_data="select  a.question_id as qid,a.question_name as qname,a.points as points,b.type_name as qtype  FROM  uls_questions a, uls_questions_type b,uls_test_questions c where( a.question_id=c.question_id and a.question_type=b.type_flag and c.test_id in (".$id.") )";
		$stmt = $pdo->prepare($emp_data);
		$stmt->execute();
		$org_values=$stmt->fetchAll();       
		return $org_values; 
        }		
	}
	
	static function get_max_count($id)
    {
		$CI =& get_instance();
		$CI->load->library('session');
        $query="select MAX(points) as maxpoints from uls_questions where parent_org_id=".$CI->session->userdata('parent_org_id')." and question_id in (select question_id from uls_test_questions where test_id=".$id.")";
        return UlsMenu::callpdorow($query);
    }
}