        $this->modified_by=$CI->session->userdata('username');
        $this->last_modified_id=$CI->session->userdata('user_id');
    }
	
	public static function ass_question_count($tid,$testype,$ass_id,$ass_test_id,$emp_id){
		$query="SELECT count(*) as numcount FROM uls_utest_questions_assessment a
		WHERE 1 and a.employee_id=".$emp_id." and assessment_id=".$ass_id." and event_id=".$ass_test_id." and test_id=".$tid." and test_type='".$testype."'";
		$masterlist=UlsMenu::callpdorow($query);		
		return $masterlist['numcount'];
    }
	
	static function question_values_inbasket($emp_id,$event,$ass_id){ 
		$sql="SELECT a.* FROM `uls_utest_questions_assessment` a 
		WHERE a.event_id=".$event." and  a.assessment_id =".$ass_id." and a.test_type='INBASKET' and a.employee_id=$emp_id";
		return UlsMenu::callpdorow($sql);
	}
