	static function searchcount($name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and position_validation_name like '%".$name."%'":" and position_validation_name like '%".$name."%'":"";
		$learning="select count(*) as num_count from uls_validation_position_definition where 1 and  parent_org_id=".$CI->session->userdata('parent_org_id').$sq;
     	$eve=UlsMenu::callpdorow($learning);                
		return $eve['num_count'];
	}
    
    static function search($start,$limit,$name=""){
		$CI =& get_instance();
		$CI->load->library('session');
		$sq=!empty($name)? !empty($sq)? " and a.position_validation_name like '%".$name."%'":" and a.position_validation_name like '%".$name."%'":"";
		$learnings="select b.value_name as val_status,a.* from uls_validation_position_definition a
		LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code
		where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." $sq limit $limit offset $start";
		$result=UlsMenu::callpdo($learnings);
		return $result;
    }
	
	static function viewpositionvalidation($id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($id) && is_numeric($id)){
			$query="select b.value_name as val_status,a.* from uls_validation_position_definition a
			LEFT JOIN (SELECT value_id, master_code, value_code, value_name FROM uls_admin_values GROUP BY value_id)b ON b.master_code = 'STATUS' AND a.status = b.value_code
			where 1 and  a.parent_org_id=".$CI->session->userdata('parent_org_id')." and a.val_id=$id";
			$compdef=UlsMenu::callpdorow($query);
		}
		return $compdef;
	}
	
	static function getemployeevalidation($emp_id){
		$CI =& get_instance();
		$CI->load->library('session');
		$compdef=array();
		if(!empty($emp_id) && is_numeric($emp_id)){
			$query="SELECT d.position_name,b.val_id,b.`position_validation_name`,b.position_validation_desc,b.start_date,b.end_date,a.* FROM `uls_validation_position_employees` a
			left join (SELECT `val_id`,`position_validation_name`,position_validation_desc,start_date,end_date FROM `uls_validation_position_definition` group by `val_id`)b on a.`val_id`=b.`val_id`
			left join (select position_id,position_name from uls_position group by position_id)d on a.position_id=d.position_id
			WHERE a.`employee_id`=$emp_id and a.parent_org_id=".$CI->session->userdata('parent_org_id')." order by a.created_date desc";
			$compdef=UlsMenu::callpdo($query);
		}
		return $compdef;
	}
